var params = [];
for (var i in msg.payload) {
    params.push(encodeURIComponent(i)+"="+encodeURIComponent(msg.payload[i]));
}
msg.querystring = params.join("&");

return msg;

// Find demo cookies and clear them
var cookieNames = Object.keys(msg.req.cookies).filter(function(cookieName) { return /^demo-/.test(cookieName);});
msg.cookies = {};

cookieNames.forEach(function(cookieName) {
    msg.cookies[cookieName] = null;
});

return msg;

// Randomly throw an error rather than
// pass on message.
if (Math.random() < 0.5) {
   node.error("a random error", msg);
} else {
    return msg;
}
